import React, {useState} from "react";
import {Container } from "react-bootstrap";
import axios from 'axios';


function Form() {
  const [course, setCourse] = useState('BLAH');
  const [workload, setWorkload] = useState(1);
  const [prof, setProf] = useState(1);

  const submitForm = async() => {
    console.log("prof is ", prof);
    await axios.post('http://localhost:4000/submitRating', {
      name: "test test name", 
      course: course, 
      prof: prof, 
    })
      .catch(() => {
        console.log("ERR in new submit form")
      })
  }

  return (
    <Container>

      <div className="mb-3">
        <label >Course Code   </label>
        <input type="coursecode" onChange={(event) => setCourse(event.target.value)} placeholder="CS-422"/>
      </div>


      <div className="row">
        <label > Professor's overall performance (clairty, organization, accessibility) </label><br></br>

        <Form>

          <Form.Check inline label="Response A" type="radio" id="radioA" value="radioA" checked={radio === "radioA"} onChange={(e) => { setRadio(e.target.value) }} />

          <Form.Check inline label="Response B" type="radio" id="radioB" value="radioB" checked={radio === "radioB"} onChange={(e) => { setRadio(e.target.value) }} />

          <Form.Check inline label="Response C" type="radio" id="radioC" value="radioC" checked={radio === "radioC"} onChange={(e) => { setRadio(e.target.value) }} />

          <Form.Check inline label="Response D" type="radio" id="radioD" value="radioD" checked={radio === "radioD"} onChange={(e) => { setRadio(e.target.value) }} />

        </Form>


      </div>


      <div class="col-12 mt-3">
        <button class="btn btn-primary" onClick={() => { submitForm() }} >Submit</button>
      </div>

    </Container>


  )

}

export default Form;
