import React, {useState} from "react";
import {Container } from "react-bootstrap";
import axios from 'axios';


function Form() {
  const [courseName, setCourseName] = useState('BLAH');
  const [courseRating, setCourseRating] = useState(0);
  const [workload, setWorkload] = useState(0);
  const [prof, setProf] = useState(0);

  const submitForm = async() => {
    console.log("prof is ", prof);
    await axios.post('http://localhost:4000/submitRating', {
      course: courseName, 
      prof: prof, 
    })
      .catch(() => {
        console.log("ERR in new submit form")
      })
  }

  return (
    <Container>

      <div className="mb-3">
        <label >Course Code</label>
        <div className="ml-3"> 
          <input  type="coursecode" onChange={(event) => setCourseName(event.target.value)} placeholder="CS-422"/>
        </div>
      </div>



      <div >
        <label > Professor's overall rating (clairty, organization, accessibility) </label><br></br>
        <div class="form-check">
          <input class="form-check-input" type="radio" checked={prof === 1} onClick={() => setProf(1)} />
            <label class="form-check-label" for="flexRadioDefault1">
              Bad
            </label>
        </div>
        <div class="form-check">
          <input class="form-check-input" type="radio" checked={prof === 2} onClick={() => setProf(2)} />
            <label class="form-check-label" for="flexRadioDefault2">
              Okay
            </label>
        </div>
        <div class="form-check">
          <input class="form-check-input" type="radio" checked={prof === 3} onClick={() => setProf(3)} />
          <label class="form-check-label" for="flexRadioDefault1">
            Good
          </label>
        </div>


      </div>


      <div class="col-12 mt-3">
        <button class="btn btn-primary" onClick={() => { submitForm() }} >Submit</button>
      </div>

    </Container>


  )

}

export default Form;
